package cs3500.imageprocessor.model.effects.blendfilter;import cs3500.imageprocessor.model.image.Pixel;import static cs3500.imageprocessor.model.functions.RepresentationConverter.convertRGBtoHSL;/** * Represents a Brighten Blend filter. This filter brightens the top layer. */public class BrightenBlend implements BlendingFilter {  private final String name = "Brighten-Blend";  @Override  public Pixel apply(Pixel pixel, Pixel bottomPixel) {    Double[] hsl = convertRGBtoHSL(            (double) pixel.getRed() / 255,            (double) pixel.getGreen() / 255,            (double) pixel.getBlue() / 255);    Double[] bottomHSL = convertRGBtoHSL(            (double) bottomPixel.getRed() / 255,            (double) bottomPixel.getGreen() / 255,            (double) bottomPixel.getBlue() / 255);    double hue = hsl[0];    double saturation = hsl[1];    double lightness = hsl[2];    double bottomLightness = bottomHSL[2];    Double[] newHSL = {hue, saturation, (1 - ((1 - lightness) * (1 - bottomLightness)))};    return BlendingFilter.getPixel(newHSL);  }  @Override  public String getName() {    return this.name;  }}