package cs3500.imageprocessor.model.effects.blendfilter;import cs3500.imageprocessor.model.image.Pixel;import static cs3500.imageprocessor.model.functions.RepresentationConverter.convertRGBtoHSL;/** * This class contains methods to darken a pixel based on its HSL value. */public class DarkenBlend implements BlendingFilter {  private final String name = "Darken-Blend";  /**   * Returns a new pixel with an updated value based on the HSL value.   *   * @param pixel the function argument   * @return a new pixel with an updated value.   */  @Override  public Pixel apply(Pixel pixel, Pixel bottomPixel) {    Double[] hsl = convertRGBtoHSL(            (double) pixel.getRed() / 255,            (double) pixel.getGreen() / 255,            (double) pixel.getBlue() / 255);    Double[] bottomHSL = convertRGBtoHSL(            (double) bottomPixel.getRed() / 255,            (double) bottomPixel.getGreen() / 255,            (double) bottomPixel.getBlue() / 255);    double hue = hsl[0];    double saturation = hsl[1];    double lightness = hsl[2];    double bottomLightness = bottomHSL[2];    Double[] newHSL = {hue, saturation, lightness * bottomLightness};    return BlendingFilter.getPixel(newHSL);  }  @Override  public String getName() {    return this.name;  }}