package cs3500.imageprocessor.model.effects.blendfilter;import cs3500.imageprocessor.model.image.Pixel;import cs3500.imageprocessor.model.image.PixelImpl;/** * Represents an Inversion filter. This filter inverts the top layer. */public class Inversion implements BlendingFilter {  private final String name = "Inversion";  /**   * Inverts the pixel based on the pixel below it.   *   * @param pixel the function argument   * @return an updated value with just the green value in the pixel.   */  public Pixel apply(Pixel pixel, Pixel bottomPixel) {    int red = Math.abs(pixel.getRed() - bottomPixel.getRed());    int green = Math.abs(pixel.getGreen() - bottomPixel.getGreen());    int blue = Math.abs(pixel.getBlue() - bottomPixel.getBlue());    return new PixelImpl(red, green, blue);  }  @Override  public String getName() {    return this.name;  }}