package cs3500.imageprocessor.model.effects.singlelayer;import java.util.function.Function;import cs3500.imageprocessor.model.image.Pixel;import cs3500.imageprocessor.model.image.PixelImpl;/** * This class contains methods to nullify or zero out the red and green values and leave only the * blue component in the pixel. */public class BlueComponent implements Function<Pixel, Pixel>, SingleLayerFilter {  private final String name = "Blue-Component";  /**   * Removes the red and green values from this pixel.   *   * @param pixel the function argument   * @return an updated value with just the blue value in the pixel.   */  @Override  public Pixel apply(Pixel pixel) {    int blue = pixel.getBlue();    return new PixelImpl(0, 0, blue);  }  @Override  public String getName() {    return this.name;  }}