package cs3500.imageprocessor.model.effects.singlelayer;import java.util.function.Function;import cs3500.imageprocessor.model.functions.FunctionUtils;import cs3500.imageprocessor.model.image.Pixel;import cs3500.imageprocessor.model.image.PixelImpl;/** * This class contains methods to brighten a pixel based on the luma value. * The Luma value is: (0.2126 * red) + (0.7152 * green) + (0.0722 * blue)) */public class BrightenLuma implements Function<Pixel, Pixel>, SingleLayerFilter {  private final String name = "Brighten-Luma";  /**   * Returns a new pixel with an updated value based to brighten it.   * @param pixel the function argument   * @return a new pixel with an updated value.   */  @Override  public Pixel apply(Pixel pixel) {    int red = pixel.getRed();    int green = pixel.getGreen();    int blue = pixel.getBlue();    int brightenLuma = (int) (.2126 * red + .7152 * green + .0722 * blue);    int newRed = FunctionUtils.clampValue(red + brightenLuma);    int newGreen = FunctionUtils.clampValue(green + brightenLuma);    int newBlue = FunctionUtils.clampValue(blue + brightenLuma);    return new PixelImpl(newRed, newGreen, newBlue);  }  @Override  public String getName() {    return this.name;  }}