package cs3500.imageprocessor.model.effects.singlelayer;import java.util.function.Function;import cs3500.imageprocessor.model.functions.FunctionUtils;import cs3500.imageprocessor.model.image.Pixel;import cs3500.imageprocessor.model.image.PixelImpl;/** * This class contains methods to darken a pixel based on the average value of the * three Red, Green, and Blue values. */public class DarkenIntensity implements Function<Pixel, Pixel>, SingleLayerFilter {  private final String name = "Darken-Intensity";  /**   * Returns a new pixel with an updated value based to darken it.   * @param pixel the function argument   * @return a new pixel with an updated value.   */  @Override  public Pixel apply(Pixel pixel) {    int red = pixel.getRed();    int green = pixel.getGreen();    int blue = pixel.getBlue();    int brightenIntensity = (red + green + blue) / 3;    int newRed = FunctionUtils.clampValue(red - brightenIntensity);    int newGreen = FunctionUtils.clampValue(green - brightenIntensity);    int newBlue = FunctionUtils.clampValue(blue - brightenIntensity);    return new PixelImpl(newRed, newGreen, newBlue);  }  @Override  public String getName() {    return this.name;  }}