package cs3500.imageprocessor.model.effects.singlelayer;import java.util.function.Function;import cs3500.imageprocessor.model.image.PixelImpl;import cs3500.imageprocessor.model.image.Pixel;/** * This class contains methods to nullify or zero out the red and blue values and leave only the * green component in the pixel. */public class GreenComponent implements Function<Pixel, Pixel>, SingleLayerFilter {  private final String name = "Green-Component";  /**   * Removes the red and blue values from this pixel.   *   * @param pixel the function argument   * @return an updated value with just the green value in the pixel.   */  @Override  public Pixel apply(Pixel pixel) {    int green = pixel.getGreen();    return new PixelImpl(0, green, 0);  }  @Override  public String getName() {    return this.name;  }}