package cs3500.imageprocessor.model.effects.singlelayer;import java.util.function.Function;import cs3500.imageprocessor.model.image.PixelImpl;import cs3500.imageprocessor.model.image.Pixel;/** * This class contains methods to nullify or zero out the blue and green values and leave only the * red component in the pixel. */public class RedComponent implements Function<Pixel, Pixel>, SingleLayerFilter {  private final String name = "Red-Component";  /**   * Removes the blue and green values from this pixel.   *   * @param pixel the function argument   * @return an updated value with just the red value in the pixel.   */  @Override  public Pixel apply(Pixel pixel) {    int red = pixel.getRed();    return new PixelImpl(red, 0, 0);  }  @Override  public String getName() {    return this.name;  }}