package cs3500.imageprocessor.model.image;import java.util.List;import cs3500.imageprocessor.model.effects.LayerFilter;/** * This interface represents a layer of images. A layer can have a filter applied to it. * The filter will be applied to all images in the layer. A layer can also have multiple images * added to it. */public interface Layer {  /**   * Returns the name of the layer.   *   * @return the name of the layer.   */  String getName();  /**   * Adds an image to the layer at the given location.   *   * @param image the image to be added.   * @param xLoc  the x location of the image.   * @param yLoc  the y location of the image.   */  void addImage(Image image, int xLoc, int yLoc);  /**   * Returns the filter of the layer.   *   * @return the filter of the layer.   */  LayerFilter getFilter();  /**   * Sets the filter of the layer.   *   * @param filter the filter of the layer.   */  void setFilter(LayerFilter filter);  /**   * Returns a list of images in the layer.   *   * @return a list of images in the layer.   */  List<Image> getImages();  /**   * Returns the width of the image.   *   * @return the width of the image.   */  int getWidth();  /**   * Returns the height of the image.   *   * @return the height of the image.   */  int getHeight();  /**   * Returns the max value of the image.   *   * @return the max value of the image.   */  int getMaxValue();  /**   * Returns the pixel at the given location.   *   * @param x the x location of the pixel.   * @param y the y location of the pixel.   * @return the pixel at the given location.   */  Pixel getPixel(int x, int y);  /**   * Returns a list of pixels in the image.   * @return a list of pixels in the image.   */  List<Pixel> getPixels();}