package cs3500.imageprocessor.view.guiview;import java.awt.event.ActionListener;import java.awt.event.ItemListener;import javax.swing.JPanel;import javax.swing.event.ListSelectionListener;import cs3500.imageprocessor.controller.guicontroller.ImageGUIController;/** * This interface represents the view for the GUI. It contains methods to refresh the view, add * features to the view, set the layer list, add to the panel, and get the left panel. */public interface ImageGUIView extends ActionListener,        ItemListener, ListSelectionListener {  /**   * Refreshes the view with the given image.   */  void refresh();  /**   * Adds features/ controller to the view.   *   * @param controller the controller to add features to the view.   */  void addFeatures(ImageGUIController controller);  /**   * Returns left panel.   *   * @return left panel   */  JPanel getLeftPanel();}