package cs3500.imageprocessor.view.guiview;import java.awt.Graphics;import java.awt.image.BufferedImage;import javax.swing.ImageIcon;import javax.swing.JLabel;import javax.swing.JPanel;/** * Simple panel to display a single image. * Displays the image using a given BufferedImage * during its refresh. */public class ImagePanel extends JPanel {  private final JLabel imageLabel;  private BufferedImage image;  /**   * Creates a new ImagePanel. Initializes the image label.   */  public ImagePanel() {    this.imageLabel = new JLabel();    this.add(imageLabel);  }  @Override  public void paintComponent(Graphics g2d) {    if (this.image != null) {      this.imageLabel.setIcon(new ImageIcon(this.image));    }  }  public void updateImage(BufferedImage image) {    this.image = image;  }}