package cs3500.imageprocessor.view.textview;import cs3500.imageprocessor.controller.textcontroller.ImageController;/** * Represents a view to display data about our images. */public class ImageViewImpl implements ImageViewInterface {  private ImageController controller;  @Override  public void addController(ImageController controller) {    this.controller = controller;  }  @Override  public void renderMessage(String message) {    System.out.println(message);  }  @Override  public void showInfo() {    controller.showInfo();  }}