package cs3500.imageprocessor.view.textview;import cs3500.imageprocessor.controller.textcontroller.ImageController;/** * Represents a view to display data about our images. Handles displaying information about the * image, saving the image, and saving the project. */public interface ImageViewInterface {  /**   * Adds controller to the view.   *   * @param controller the controller to add features to the view.   */  void addController(ImageController controller);  /**   * Saves the project to a specified location with a specified name.   *   * @param message the message to be displayed   */  void renderMessage(String message);  /**   * Displays the information about the image.   */  void showInfo();}